#include<iostream>using namespace std;void increment(int *p){	*p = *p + 1;	cout << "Adress Passed in the Function Through Pointer   " << p << endl;}int main(){#ifndef ONLINE_JUDGE	freopen("input.txt", "r", stdin);	freopen("output.txt", "w", stdout);#endif	int a;	a = 10;	cout << "Adress in Main Function    " << &a << endl;	cout << "Intial Value " << a << endl;	increment(&a);	cout << "Incremented Value From Function " << a << endl;}